INSERT INTO questions (quiz_id, question_text, options, correct_answer, difficulty_level, points, explanation) VALUES
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is an algorithm?', '["A computer virus", "A step-by-step procedure to solve a problem", "A type of computer", "A programming language"]', 'A step-by-step procedure to solve a problem', 'easy', 1, 'An algorithm is a sequence of steps to solve a specific problem'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'Which of these is a programming language?', '["Microsoft Word", "Python", "Google Chrome", "Windows"]', 'Python', 'easy', 1, 'Python is a popular programming language used for various applications'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What does CPU stand for?', '["Central Program Unit", "Central Processing Unit", "Computer Personal Unit", "Control Processing Unit"]', 'Central Processing Unit', 'easy', 1, 'CPU is the brain of the computer that processes instructions'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is a variable in programming?', '["A type of computer", "A storage location with a name", "A computer game", "A website"]', 'A storage location with a name', 'easy', 1, 'Variables are used to store data in programming'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is the purpose of a loop in programming?', '["To end a program", "To repeat certain actions", "To delete files", "To print text"]', 'To repeat certain actions', 'easy', 1, 'Loops are used to repeat a block of code multiple times'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is debugging?', '["Creating a program", "Finding and fixing errors", "Running a program", "Writing code"]', 'Finding and fixing errors', 'easy', 1, 'Debugging is the process of identifying and fixing errors in code'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is binary code?', '["A computer virus", "A system using only 0s and 1s", "A programming language", "A type of computer"]', 'A system using only 0s and 1s', 'easy', 1, 'Binary is the basic language of computers using only two digits'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is an input device?', '["Monitor", "Printer", "Keyboard", "Speaker"]', 'Keyboard', 'easy', 1, 'Input devices like keyboards are used to enter data into a computer'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What does RAM stand for?', '["Random Access Memory", "Read Always Memory", "Run Automatic Memory", "Right Access Module"]', 'Random Access Memory', 'easy', 1, 'RAM is temporary memory used by computers to store working data'),
((SELECT id FROM quizzes WHERE title = 'Basic Programming'), 'What is a file extension?', '["A computer brand", "A type of computer", "Characters after dot in filename", "A programming language"]', 'Characters after dot in filename', 'easy', 1, 'File extensions indicate the type of file, like .txt or .doc');
INSERT INTO questions (quiz_id, question_text, options, correct_answer, difficulty_level, points, explanation) VALUES
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the basic principle of a lever?', '["Converting energy", "Multiplying force", "Storing data", "Generating electricity"]', 'Multiplying force', 'easy', 1, 'A lever is a simple machine that multiplies force through mechanical advantage'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What does CAD stand for?', '["Computer Aided Design", "Computer Automatic Drawing", "Complex Art Design", "Computer Art Development"]', 'Computer Aided Design', 'easy', 1, 'CAD is used by engineers to create precise technical drawings'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the purpose of a blueprint?', '["To paint buildings", "To provide construction details", "To decorate walls", "To measure temperature"]', 'To provide construction details', 'easy', 1, 'Blueprints are technical drawings that guide construction'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'Which tool is used to measure angles?', '["Ruler", "Protractor", "Scale", "Compass"]', 'Protractor', 'easy', 1, 'A protractor is used to measure and draw angles'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the main purpose of a gear?', '["To store energy", "To transfer motion", "To generate heat", "To produce light"]', 'To transfer motion', 'easy', 1, 'Gears transfer rotational motion and can change speed or direction'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the basic unit of force?', '["Watt", "Newton", "Volt", "Ampere"]', 'Newton', 'easy', 1, 'Newton (N) is the SI unit of force'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the purpose of insulation?', '["To generate power", "To control temperature", "To increase weight", "To produce light"]', 'To control temperature', 'easy', 1, 'Insulation helps control heat transfer between materials'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is efficiency in engineering?', '["Speed of work", "Cost of project", "Useful output/total input ratio", "Size of machine"]', 'Useful output/total input ratio', 'easy', 1, 'Efficiency measures how well resources are converted to useful output'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the purpose of a safety factor?', '["To make things cheaper", "To add extra margin of safety", "To increase speed", "To save energy"]', 'To add extra margin of safety', 'easy', 1, 'Safety factors ensure designs can handle more than expected loads'),
((SELECT id FROM quizzes WHERE title = 'Engineering Basics'), 'What is the main function of a pulley?', '["To store energy", "To lift loads easily", "To generate electricity", "To measure weight"]', 'To lift loads easily', 'easy', 1, 'Pulleys make it easier to lift heavy loads by changing force direction');
